@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --bg: theme(colors.cream);
  --fg: theme(colors.ink);
}

html, body { 
  scroll-behavior: smooth;
  margin: 0;
  padding: 0;
}

body { 
  background: 
    linear-gradient(135deg, #f8f6f0 0%, #e8e5dc 25%, #f0ede4 50%, #e2ddd1 75%, #f5f3ed 100%),
    radial-gradient(circle at 20% 50%, rgba(18, 51, 46, 0.05) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, rgba(156, 149, 124, 0.08) 0%, transparent 50%),
    radial-gradient(circle at 40% 80%, rgba(18, 51, 46, 0.03) 0%, transparent 50%);
  background-attachment: fixed;
  background-size: 100% 100%, 800px 800px, 600px 600px, 900px 900px;
  color: var(--fg);
  font-feature-settings: "rlig" 1, "calt" 1;
  position: relative;
}

/* Enhanced dynamic background overlay with subtle patterns */
body::before {
  content: '';
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: 
    linear-gradient(45deg, transparent 40%, rgba(18, 51, 46, 0.02) 50%, transparent 60%),
    linear-gradient(-45deg, transparent 40%, rgba(156, 149, 124, 0.03) 50%, transparent 60%),
    radial-gradient(400px circle at 30% 20%, rgba(18, 51, 46, 0.04) 0%, transparent 50%),
    radial-gradient(300px circle at 70% 80%, rgba(156, 149, 124, 0.05) 0%, transparent 50%);
  background-size: 200px 200px, 150px 150px, 400px 400px, 300px 300px;
  animation: backgroundShift 20s ease-in-out infinite;
  pointer-events: none;
  z-index: 1;
}

/* Subtle background animation */
@keyframes backgroundShift {
  0%, 100% {
    transform: translate(0, 0);
    opacity: 1;
  }
  25% {
    transform: translate(-10px, -5px);
    opacity: 0.8;
  }
  50% {
    transform: translate(5px, -10px);
    opacity: 0.9;
  }
  75% {
    transform: translate(-5px, 5px);
    opacity: 0.85;
  }
}

.container { 
  @apply max-w-7xl mx-auto px-4 lg:px-6; 
}

.btn { 
  @apply inline-flex items-center justify-center rounded-xl px-6 py-3 font-semibold transition-all duration-200 transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-offset-2; 
}

.btn-brand { 
  @apply btn bg-gradient-to-r from-brand to-brand/90 text-white hover:from-brand/90 hover:to-brand shadow-lg hover:shadow-xl focus:ring-brand/50; 
}

.card { 
  @apply bg-white/90 backdrop-blur-sm rounded-2xl shadow-lg border border-white/20 hover:shadow-xl transition-all duration-300; 
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: #e9e7e2;
}

::-webkit-scrollbar-thumb {
  background: rgba(18, 51, 46, 0.4);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: rgba(18, 51, 46, 0.6);
}

/* Custom selection */
::selection {
  background: rgba(18, 51, 46, 0.2);
  color: #12332e;
}

/* Enhanced animations */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px) rotate(0deg);
  }
  50% {
    transform: translateY(-20px) rotate(5deg);
  }
}

@keyframes shimmer {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(100%);
  }
}

@keyframes pulseRing {
  0% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.05);
    opacity: 0.7;
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}

@keyframes glow {
  0%, 100% {
    box-shadow: 0 0 20px rgba(18, 51, 46, 0.3);
  }
  50% {
    box-shadow: 0 0 30px rgba(18, 51, 46, 0.6);
  }
}

/* Enhanced utility classes */
.animate-fade-in {
  animation: fadeInUp 0.8s ease-out forwards;
}

.floating-particle {
  animation: float 12s ease-in-out infinite;
}

.shimmer {
  animation: shimmer 2s infinite;
}

.animate-pulse-ring {
  animation: pulseRing 3s ease-in-out infinite;
}

.animate-glow {
  animation: glow 2s ease-in-out infinite;
}

/* Hero specific animations */
.hero-loading .hero-title {
  opacity: 0;
  transform: translateY(30px);
}

.hero-loaded .hero-title {
  opacity: 1;
  transform: translateY(0);
  animation: slideInLeft 1s ease-out 0.2s forwards;
}

.hero-loading .hero-subtitle {
  opacity: 0;
  transform: translateY(30px);
}

.hero-loaded .hero-subtitle {
  opacity: 1;
  transform: translateY(0);
  animation: slideInLeft 1s ease-out 0.4s forwards;
}

.hero-loading .hero-buttons {
  opacity: 0;
  transform: translateY(30px);
}

.hero-loaded .hero-buttons {
  opacity: 1;
  transform: translateY(0);
  animation: slideInLeft 1s ease-out 0.6s forwards;
}

.hero-loading .hero-image {
  opacity: 0;
  transform: translateX(50px) scale(0.95);
}

.hero-loaded .hero-image {
  opacity: 1;
  transform: translateX(0) scale(1);
  animation: slideInRight 1s ease-out 0.4s forwards;
}

/* Enhanced product card animations */
.product-card-enhanced {
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.product-card-enhanced:hover {
  transform: translateY(-8px) scale(1.02);
}

/* Enhanced button effects */
.btn-enhanced {
  position: relative;
  overflow: hidden;
  transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.btn-enhanced::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
  transition: left 0.6s;
}

.btn-enhanced:hover::before {
  left: 100%;
}

/* Gradient text utility */
.gradient-text {
  @apply bg-gradient-to-r from-brand to-brand/80 bg-clip-text text-transparent;
  line-height: 1.2;
  padding-bottom: 0.15rem;
  display: inline-block;
}

/* Modern glass effect */
.glass {
  @apply bg-white/10 backdrop-blur-md border border-white/20;
}

/* Enhanced glass morphism */
.glass-card {
  background: rgba(255, 255, 255, 0.15);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
}

/* Interactive hover glow */
.hover-glow:hover {
  box-shadow: 0 0 30px rgba(18, 51, 46, 0.3);
  transition: box-shadow 0.3s ease;
}

